v=1;w=4;M=4;ab={0,31,0,255}aB={0,15,0,255}aC={31,31,31,255}R={0,255,0,255}aD=10;aE=32;ac=180;ad=aF.getBool;o=aF.getNumber;h=b9;a1=h.pi;s=2*a1;x=h.sin;p=h.cos;ba=h.tan;aG=h.asin;bb=h.acos;aH=h.atan;S=a1/180;y=aI;aJ=y.setColor;aK=y.drawText;C=y.drawLine;bc=y.drawCircle;ae=y.drawRect;aL=aM.format;af=bd;aN=af.insert;ag=af.remove;i=aO.getNumber;ah=aO.getBool;aP=ah("Show Map Scale")aQ=i("Map Scale Horizontal Position")aR=i("Map Scale Vertical Position")be=ah("Marge close targets")f=i("Target Map Style")N=i("Display Range Unit")k={i("Display Range 1"),i("Display Range 2"),i("Display Range 3"),i("Display Range 4")}ai=i("Min display azim. angle (deg)")*S;aj=i("Max display azim. angle (deg)")*S;ak=i("Min display elev. angle (deg)")*S;al=i("Max display elev. angle (deg)")*S;function T(aS,aT)q=0;for a=1,3 do q=q+(aS[a]-aT[a])^2 end;return q^0.5 end;function aU(D,t)e={}e[1]=D[2]*t[3]-D[3]*t[2]e[2]=D[3]*t[1]-D[1]*t[3]e[3]=D[1]*t[2]-D[2]*t[1]return e end;function aV(l)am={p(l[1])*x(-l[2]),p(l[1])*p(-l[2]),x(l[1])}an={p(l[3])*x(-l[4]),p(l[3])*p(-l[4]),x(l[3])}aW=aU(am,an)return{am,an,aW}end;function aX(aY,t)ao={}for a=1,3 do q=0;for g=1,3 do q=q+aY[g][a]*t[g]end;ao[a]=q end;return ao end;function aZ(a2)a3,a4,a5=a2[1],a2[2],a2[3]E,F,G,H,I,J,K,c,a=a3[1],a4[1],a5[1],a3[2],a4[2],a5[2],a3[3],a4[3],a5[3]j=E*I*a+H*c*G+K*F*J-K*I*G-E*c*J-H*F*a;a_={(I*a-J*c)/j,(J*K-H*a)/j,(H*c-I*K)/j}b0={(G*c-F*a)/j,(E*a-G*K)/j,(F*K-E*c)/j}b1={(F*J-G*I)/j,(G*H-E*J)/j,(E*I-F*H)/j}return{a_,b0,b1},j end;function ap(aq)aq[4]=0;aN(b,aq)if#b>aE then ag(b,1)end end;function b2(U)V={}V[1]=T(U,{0,0,0})V[2]=aH(U[1],U[2])V[3]=aG(U[3]/q)return V end;function ar(as,at,W,a6)for a=1,a6 do au,av=(a-1)*s/a6,a*s/a6;C(as+W*p(au),at-W*x(au),as+W*p(av),at-W*x(av))end end;function O(X)aJ(X[1],X[2],X[3],X[4])end;z={}b={}e,c=8;m=1;aw=false;function onTick()if f~=0 then a7={o(4),o(8),o(12)}b3={o(16)*s,o(20)*s,o(24)*s,o(28)*s}b4=aV(b3)b5,q=aZ(b4)bf=o(32)*s;ax=ad(32)for a=1,8 do j=ad(a)if j then for g=1,3 do z[a][g]=o(a*4-(4-g))end;if#b>0 then a8,a9=T(b[1],z[a]),1;for d=1,#b do ay=T(b[d],z[a])if ay<a8 then a8=ay;a9=d end end;if a8<aD then for g=1,3 do b[a9][g]=z[a][g]end;b[a9][4]=0 else ap(z[a])end else ap(z[a])end else z[a]={0,0,0}end end;if ax and not aw then for a=1,#k do m=m%#k+1;if k[m]>0 then break end end end;aw=ax;if#b>0 then for d=#b,1,-1 do b[d][4]=b[d][4]+1;if b[d][4]>ac then ag(b,d)end end end end end;function onDraw()if f~=0 then e=y.getWidth()c=y.getHeight()-1;if f==1 or f==2 then r=c/(2*k[m]*N)n=-r elseif f==3 then r=c/(2*k[m]*N)n=-r elseif f==4 then r=e/(2*h.max(h.abs(ai),h.abs(aj)))if v==1 then n=-c/(k[m]*N)else n=-c/(2*h.max(h.abs(ak),h.abs(al)))end else end;if#b>0 then for d=1,#b do P={}for g=1,3 do P[g]=b[d][g]-a7[g]end;if f==1 or f==2 then b[d][5]=P[1]*r;b[d][6]=P[2]*n else Y=aX(b5,P)if f==3 then b[d][5]=Y[1]*r;if v==1 then b[d][6]=Y[2]*n else b[d][6]=Y[3]*n end elseif f==4 then aa=b2(Y)b[d][5]=aa[2]*r;if v==1 then b[d][6]=aa[1]*n else b[d][6]=aa[3]*n end end end;b[d][7]=T(P,{0,0,0})end end;O(aB)if f==1 or f==2 or f==3 then A,L,u,B=0,e,c,0;if f==2 then aI.drawMap(a7[1],a7[2],2*k[m]*N/10^3)end;if w>0 then for a=1,w do az=a1/2;aA=s/w*a;C(e/2,c/2,e/2+c/2*p(az+aA),c/2-c/2*x(az+aA))end end;for a=1,M do b6=c/2/(M+1)*a;ar(e/2,c/2,b6,36)end;O(ab)ar(e/2,c/2,c/2,36)elseif f==4 then A,L=e/2+ai*r,e/2+aj*r;if v==1 then u,B=c,0 else u,B=c/2+ak*n,c/2+al*n end;if w>0 then for a=1,w do for g=-1,1,2 do if v==1 then Q=c/(w+1)*a else Q=c/2+g*c/2/(w+1)*a end;if Q<u and Q>B then C(A,Q,L,Q)end end end end;if M>0 then for a=1,M do for g=-1,1,2 do Z=e/2+g*e/2/(M+1)*a;if Z>A and Z<L then C(Z,u,Z,B)end end end end;O(ab)C(e/2,u,e/2,B)ae(A,u,L-A-1,B-u)if v==2 then C(A,c/2,L,c/2)end end;if aP then O(aC)b7=aM.len(k[m])-2;aK((e-5*b7)*aQ,(c-5)*aR,aL("%d",k[m]))end;if#b>0 then for d=1,#b do if b[d][7]<k[m]*N then b8={R[1],R[2],R[3],R[4]*(1-b[d][4]/ac)}O(b8)if f==4 and v==1 then _,a0=e/2+b[d][5],c+b[d][6]else _,a0=e/2+b[d][5],c/2+b[d][6]end;if _>A and _<L and a0<u and a0>B then ae(_,a0,0.5,0.5)end end end en